#!/bin/bash

while : ; do read -p "Enter new User login name (or Ctrl+c to end): " USERID; read -p "Enter the existing OU to create the user in: " OU_NAME; read -p "Enter the SSHA hash of the user's password (if needed, use: http://projects.marsching.org/weave4j/util/genpassword.php: " PASSWORD_HASH; read -p "Enter new User's first name: " FIRST_NAME; read -p "Enter new User's last name: " SURNAME; read -p "Enter new User's full name: " FULL_NAME; read -p "Enter new User's email address: " EMAIL_ADDRESS; echo ""; LDIF_UPDATE_FILE=3add_user-02.Dec.2019.08.45.28.ldif; cp -p templates/add_user_template.ldif updates-processing/; source container_variables.txt && sed -i "s/USERID//; s/OU_NAME//; s/SUFFIX//; s/PASSWORD_HASH//; s/FIRST_NAME//; s/SURNAME//; s/FULL_NAME//; s/EMAIL_ADDRESS//" updates-processing/; done
